var CT = {} || CT;

CT.MAIN = (function() {
  return {
    init: function() {
      // Jingle
      function addOpacity(parent, son) {
        $(parent)
          .find(son)
          .removeClass("o-0");
      }

      // var timeoutTwo = window.setTimeout(function() {
      //   addOpacity(".js-jingle-one", "li:nth-of-type(1)");
      // }, 700);
      // var timeoutTwo = window.setTimeout(function() {
      //   addOpacity(".js-jingle-one", "li:nth-of-type(2)");
      // }, 1400);
      // var timeoutTwo = window.setTimeout(function() {
      //   addOpacity(".js-jingle-one", "li:nth-of-type(3)");
      // }, 2100);

      setTimeout(function() {
        $(".js-one")
          .removeClass("h-0")
          .addClass("h-120");
      }, 2500);

      setTimeout(function() {
        $(".js-two").fadeIn(500);
        $(".js-one")
          .removeClass("h-120")
          .addClass("h-156");
      }, 3500);

      setTimeout(function() {
        $(".js-three").fadeIn(500);
        $(".js-one")
          .removeClass("h-156")
          .addClass("h-192");
      }, 5000);

      setTimeout(function() {
        $(".js-four").fadeIn(500);
        $(".js-one")
          .removeClass("h-192")
          .addClass("h-228");
      }, 6500);

      setTimeout(function() {
        $(".js-five").fadeIn(500);
        $(".js-one")
          .removeClass("h-228")
          .addClass("h-264");
      }, 8000);

      setTimeout(function() {
        $(".js-six").fadeIn(500);
        $(".js-one")
          .removeClass("h-264")
          .addClass("h-300");
      }, 9500);

      // Card
      $(".js-card").on("click", function() {
        var elem = $(this);
        $(this)
          .parent()
          .addClass("active");

        setTimeout(function() {
          $(elem)
            .parent()
            .addClass("opened");
        }, 1000);
      });

      $(".js-close").on("click", function() {
        $(this)
          .parents(".card")
          .removeClass("active")
          .removeClass("opened");
      });
    }
  };
})();

function ready(fn) {
  if (document.readyState === "complete") {
    CT.MAIN.init();
  } else {
    document.addEventListener("DOMContentLoaded", fn);
  }
}

ready(CT.MAIN.init);
