var CT = {} || CT;

CT.MAIN = (function() {
  return {
    init: function() {
      $(".js-card").on("click", function() {
        var elem = $(this);
        $(this)
          .parent()
          .addClass("active");

        setTimeout(function() {
          $(elem)
            .parent()
            .addClass("opened");
        }, 1000);
      });

      $(".js-close").on("click", function() {
        $(this)
          .parents(".card")
          .removeClass("active")
          .removeClass("opened");
      });
    }
  };
})();

function ready(fn) {
  if (document.readyState === "complete") {
    CT.MAIN.init();
  } else {
    document.addEventListener("DOMContentLoaded", fn);
  }
}

ready(CT.MAIN.init);
